(function ($) {
  Drupal.behaviors.mwaa = {
    attach: function (context, settings) {
      if (document.implementation.hasFeature("http://www.w3.org/TR/SVG11/feature#Shape", "1.0")) {
        $('.site-name').addClass('svg');
      }
      else {
        $('.site-name').addClass('no-svg');
      }

      jQuery('.view-display-id-security .views-row-odd').last().css("border-bottom", "none");
      $(context).bind('cbox_complete', function () {
        var imgTitle = $('#cboxTitle').text();
        var titleArray = imgTitle.split("|||");
        var url = $("a[title='"+imgTitle+"']").attr("url");
        $('#cboxTitle').html('<div class="photo_gallery"><div class="image_caption_group"><div class="image_title">' + titleArray[0] + '</div><div class="image_desc">' + titleArray[1] + '</div></div><div class="image_download_group"><div class="image_download_desc"><a href="' + url + '" download>Download</a></div><div class="image_download_desc"><a href="' + Drupal.settings.basePath + 'about/use-images-airports-authority-website?fid=' + titleArray[3] + '">Request commercial use</a></div></div><div>');

        if ($('#cboxTitle:empty', context).length == false) {
          $('#cboxLoadedContent img', context).unbind('mouseover');
          $('#cboxOverlay', context).unbind('mouseover');
        }
      });
      if ($.trim($('section.block .view-mwaa-timing').html()).length) {
        var block_id = $('section.block .view-mwaa-timing').parent().attr('id');
        $('#' + block_id).hide();
        var now = Date.now();
        var code = Drupal.settings.site_section_code;
        $('#' + block_id).load("/load_mwaa_timing/" + code.toLowerCase()  + "/security/" + now, function (res, status, xhr) {
          var data = $('#' + block_id).html();
          $('body:not(.node-type-home-page) section.block .view-mwaa-timing').parent().remove();
          $('.field-body').prepend(data);
          $('#' + block_id).show();
        });
      }
      $('.group-photo-gallery-caption a', context).click(function(event) {
        event.preventDefault();
        $(this).closest('.group-photo-gallery-caption').siblings('.field-image').find('a').trigger('click');
        $(this).closest('.field-image a').trigger('click');
      });
      
      // Fixed position for secondary navigation.
      if ($.trim($('#whole_secondary_menu, #whole_header_top').html()).length) {
        setTimeout(function() {
          
          var navpos = 0;
          var header_height = 0;
          
          var sizeIt = function() {
            navpos = $('#whole_header_top').offset().top + $('#whole_header_top').height();
            // Default with both menus
            header_height = $('#whole_secondary_menu').height() + $('#whole_header_top').height();
            if ($('#whole_secondary_menu').css('display') == 'none') { // Mobile mode
              navpos = 0;
              header_height = $('#whole_header_top').height();
            }
          }
          
          sizeIt();
          $(window).resize(sizeIt);
          
          $('.alert .close').click(function() {
            // Give jQuery a few moments to remove the alerts section.
            //  Then fetch the new figures.
            setTimeout(function() {
              navpos = $('#whole_header_top').offset().top + $('#whole_header_top').height();
              header_height = $('#whole_secondary_menu').height() + $('#whole_header_top').height();
            }, 50);
          });
          $(window).bind('scroll', function(){
            if ($(window).scrollTop() > navpos) {
              $('#whole_secondary_menu, #whole_header_top').addClass("secondary_menu_fixed");
              $('#page-header').next('div').css('margin-top', Math.ceil(header_height)+'px');
            }
            else {
              $('#whole_secondary_menu, #whole_header_top').removeClass("secondary_menu_fixed");
              $('#page-header').next('div').css('margin-top', '');
            }
          });
        }, 100);
      }
      
      // MWRP2-254: use take-me-there class to trigger js interaction
      var $takeMeThere = $('a.take-me-there');
      $takeMeThere.each(function(index, item) {
        if (!$(item).attr('title')) $(item).attr('title', Drupal.t('Take me to '+$(item).text()));
      });
      $takeMeThere.click(function(ev) {
        ev.preventDefault();
        window.open($(this).attr('href'), '_blank').focus();
      });
    }
  };
})(jQuery);
