function resizeRow44RoadblockAd(show) {
    show = typeof show !== 'undefined' ? show : true;

    // Make sure roadblockad covers entire page, since that is what greys it out.
    jQuery('#roadblockad').height(jQuery('#wrapper').height());

    // Add a 10px border around the window.
    jQuery('#rblockholder').css({'border':'5px solid black'}).height(jQuery(window).height() - 10).width(jQuery(window).width() - 10);

    var pos = 'fixed';
    var winHeight = jQuery(window).height();
    var rblockHeight = jQuery('#rblockImage').height() + 162 + 40;
    // 162 is the number of pixels for the Advertisement text and Continue button.  For some reason, jQuery does
    // not always report the proper height of the rblock div, so that is why am hard-coding that in.
    // 40 is to make room for a 5px border and 15px margin on each side of the image.

    if (rblockHeight < winHeight - 5) {
        jQuery('#rblockImage').height(500).width(935);
    }
    else {
        var ratio = 500 / 935.0; // Get the ratio of the image.
        var diff = rblockHeight - winHeight;
        var newHeight = jQuery('#rblockImage').height() - diff;
        if (newHeight < 125) {
            newHeight = 125;
            pos = 'absolute';
        }
        var newWidth = newHeight / ratio;
        jQuery('#rblockImage').height(newHeight);
        jQuery('#rblockImage').width(newWidth);
        jQuery('.rblockDiv').width(newWidth);
    }
    jQuery('#roadblockad').css({'position': pos});
    if (pos == 'absolute') {
        jQuery('#rblockholder').css({'border-bottom':'0px solid black'});
    }
    if (show) {
        jQuery('#rblockholder').show();
    }
}

