function logLocalAd(obj, adType, adAction, nextPage) {
    try {
        var logUrl = location.protocol + '//' + location.host + '/logAdData';
        nextUrl = nextPage || '';

        jQuery.ajax({
            url: logUrl,
            data: { 'adCampaign': obj.data('campaign'), 'adImage': obj.data('image'), 'adId': obj.data('adid'), 'adsov': obj.data('adsov'), 'adType': adType, 'adAction': adAction, 'page': location.pathname },
            dataType: 'json',
            timeout: 10000,
            type: 'GET',
            success: function(){
                if (nextUrl) window.location = nextUrl;
            },
            error: function(results){
                if (nextUrl) window.location = nextUrl;
            }
        });
    }
    catch(e) {
        if (nextUrl) window.location = nextUrl;
    }
}

function processLocalAdClick(e, obj, lnk, adType) {
//alert('processLocalAdClick: href = ' + lnk.attr('href') + ', target = ' + lnk.attr('target'));
    if (lnk.attr('target') == '_self') {
        if (e) {
            e.preventDefault();
        }
        logLocalAd(obj, adType, 'click', lnk.attr('href'));
    }
    else {
        logLocalAd(obj, adType, 'click');
    }
}

