// fancybox modal popup init
function initLightbox() {
	jQuery('a.lightbox, a[rel*="lightbox"]').each(function(){
		var link = jQuery(this);
        /* added (15th Oct 2014) */
        // when clicking on a lightbox class element that opens a fancybox modal, determine if option modal is true or false
        // this prevents the user of closing the modal without selecting any options that will close it
        modalOption = false;
        if (link.attr('data-fancybox-modal') === "true"){
            modalOption = true;
        }
        /* End: added (15th Oct 2014) */
		link.fancybox({
			padding: 0,
			cyclic: false,
			overlayShow: true,
			hideOnOverlayClick: false,
			overlayOpacity: 0.65,
			overlayColor: '#000000',
			titlePosition: 'inside',
            modal: modalOption, /* added (15th Oct 2014): */
			onStart: function(box) {
                hrefValue = link.attr('href');

                if (hrefValue == '#beats-terms-of-use-popup') {
                    // Load the popup content.
                    jQuery("#beats-terms-of-use-popup-content").load("/beats_popup_load.jq?p=beats-terms-of-use-popup");
				}
                else if (hrefValue == '#beats-privacy-policy-popup') {
                    // Load the popup content.
                    jQuery("#beats-privacy-policy-popup-content").load("/beats_popup_load.jq?p=beats-privacy-policy-popup");
				}
                else if (hrefValue == '#ebook-terms-of-use-popup') {
                    // Load the popup content.
                    jQuery("#ebook-terms-of-use-popup-content").load("/ebook_popup_load.jq?p=ebook-terms-of-use-popup");
				}
                else if (hrefValue == '#ebook-privacy-policy-popup') {
                    // Load the popup content.
                    jQuery("#ebook-privacy-policy-popup-content").load("/ebook_popup_load.jq?p=ebook-privacy-policy-popup");
				}
                else if (hrefValue == '#popup-registration-login') {
                    // Hide any error messages.
                    jQuery('#popup-registration-login #errorMsgExc1, #popup-registration-login #errorMsgExc2, #popup-registration-login #errorMsgMisc').hide();
				}
                else if (hrefValue == '#ebooks-email-popup') {
                    if (link.hasClass('ebooks-register')) {
                        jQuery('#ebooks-email-popup .ebooks-newsletter-signup').hide();
                        jQuery('#ebooks-email-popup .ebooks-end-of-book').hide();
                        jQuery('#ebooks-email-popup .ebooks-register').show();
                    }
                    else if (link.hasClass('ebooks-newsletter-signup')) {
                        jQuery('#ebooks-email-popup .ebooks-register').hide();
                        jQuery('#ebooks-email-popup .ebooks-end-of-book').hide();
                        jQuery('#ebooks-email-popup .ebooks-newsletter-signup').show();
                    }
                    else if (link.hasClass('ebooks-end-of-book')) {
                        jQuery('#ebooks-email-popup .ebooks-register').hide();
                        jQuery('#ebooks-email-popup .ebooks-newsletter-signup').hide();
                        jQuery('#ebooks-email-popup .ebooks-end-of-book').show();
                    }
				}
			},
			onComplete: function(box) {
                hrefValue = link.attr('href');

				if (hrefValue.indexOf('#') === 0) {
					jQuery('#fancybox-content').find('a.close, button.close').unbind('click.fb').bind('click.fb', function(e){
						jQuery.fancybox.close();
						e.preventDefault();
					});
				}

                /* added (15th Oct 2014) */
                /* Special processing for popups on music page.  The following
                   assumes that #beats-terms-of-use-popup and #beats-privacy-policy-popup
                   are called only from other certain popups.
                 */
                if (hrefValue == '#popup-registration-user' || hrefValue == '#popup-registration-login' || hrefValue == '#popup-registration-forced') {
                    // Remember which popup is being displayed.
                    beats.popupLink = hrefValue;
                }
                else if (hrefValue == '#beats-terms-of-use-popup' || hrefValue == '#beats-privacy-policy-popup') {
                    // Set the href values of the close links to the
                    // previous popup that was displayed.
                    jQuery('.beats-agreement-popup a.close-link').attr('href', beats.popupLink);
                }
                /* End: added (15th Oct 2014) */

                // If there is a user-defined callback function, call it.
                if (typeof lightbox_complete_callback == 'function') {
                    lightbox_complete_callback(hrefValue);
                }
			},
/*
			onCleanup: function(box) {
			},
			onCancel: function(box) {
			},
*/
			onClosed: function(box) {
                hrefValue = link.attr('href');

                // If there is a user-defined callback function, call it.
                if (typeof lightbox_closed_callback == 'function') {
                    lightbox_closed_callback(hrefValue);
                }
			},
		});
	});
}

